mapper.update:
UPDATE doc
SET d_title = #{d_title},
d_content = #{d_content},
d_summary = #{d_summary},
u_id = #{u_id}
WHERE d_num = #{d_num}

UPDATE doc
SET p_read = #{p_read},
p_update = #{p_update},
p_delete = #{p_delete}
WHERE d_num = #{d_num}

mapper.insert:
INSERT INTO doc_history (d_num, d_version, d_date, u_id, d_summary, d_content)
SELECT #{d_num}, (SELECT IFNULL(MAX(d_version), 0) FROM doc_history WHERE d_num = #{d_num}) + 1, SYSDATE(), #{u_id}, #{d_summary}, #{d_content}
FROM doc
WHERE d_num = #{d_num}

mapper.update:
UPDATE doc
SET d_available = 0, d_content = '삭제된 문서입니다.'
WHERE d_num = #{d_num}
