mapper.select:
select *
from user
where u_id=#{u_id} and pwd=#{pwd}

mapper.update:
UPDATE user
SET p_id = #{p_id}
where u_id = #{u_id}

UPDATE user
SET ban = 1, ban_date = SYSDATE(), ban_reason = #{ban_reason}
where u_id = #{u_id}

UPDATE user
SET ban = 0, ban_date = null, ban_reason = null
where u_id = #{u_id}
